import React from 'react';

const Note = ({ note, onDelete, onEdit }) => {
  const { id, title, content, category } = note;

  const categoryColors = {
    personal: 'bg-blue-100 border-blue-500',
    work: 'bg-green-100 border-green-500',
    urgent: 'bg-red-100 border-red-500'
  };

  return (
    <div className={`p-4 border-l-4 ${categoryColors[category]} rounded-md shadow-md mb-4`}>
      <h3 className="text-lg font-semibold">{title}</h3>
      <p className="text-gray-700">{content}</p>
      <div className="flex justify-end mt-2">
        <button onClick={() => onEdit(id)} className="text-blue-500 hover:underline mr-2">Edit</button>
        <button onClick={() => onDelete(id)} className="text-red-500 hover:underline">Delete</button>
      </div>
    </div>
  );
};

export default Note;
