import React from 'react';

const CategoryFilter = ({ filter, setFilter }) => {
    return (
        <div className="category-filter mb-4 flex justify-center gap-2">
            <button onClick={() => setFilter('')} className={`button ${filter === '' ? 'bg-gray-500 text-white' : 'bg-gray-300'} hover:bg-gray-400`}>
                Todas
            </button>
            <button onClick={() => setFilter('work')} className={`button ${filter === 'work' ? 'bg-gray-500 text-white' : 'bg-gray-300'} hover:bg-gray-400`}>
                Trabajo
            </button>
            <button onClick={() => setFilter('personal')} className={`button ${filter === 'personal' ? 'bg-gray-500 text-white' : 'bg-gray-300'} hover:bg-gray-400`}>
                Personal
            </button>
            <button onClick={() => setFilter('tasks')} className={`button ${filter === 'tasks' ? 'bg-gray-500 text-white' : 'bg-gray-300'} hover:bg-gray-400`}>
                Tareas
            </button>
            <button onClick={() => setFilter('other')} className={`button ${filter === 'other' ? 'bg-gray-500 text-white' : 'bg-gray-300'} hover:bg-gray-400`}>
                Otro
            </button>
        </div>
    );
};

export default CategoryFilter;
