import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const categoryIcons = {
    personal: 'icon-personal.png',
    work: 'icon-work.png',
    tasks: 'icon-tasks.png',
    other: 'icon-other.png',
};

const Chart = ({ notes }) => {
    const categories = ['personal', 'work', 'tasks', 'other'];
    const categoryColors = {
        personal: '#8eb8e5',
        work: '#d3b8ae',
        tasks: '#6cbf6f',
        other: '#d6b8db',
    };

    const categoryCounts = categories.map(category => notes.filter(note => note.category === category).length);

    const barData = {
        labels: categories,
        datasets: [{
            label: 'Número de Notas',
            data: categoryCounts,
            backgroundColor: Object.values(categoryColors),
        }]
    };

    const pieData = {
        labels: categories,
        datasets: [{
            data: categoryCounts,
            backgroundColor: Object.values(categoryColors),
        }]
    };

    const drawIcons = (chart) => {
        if (!chart.chartArea) return; // Check if chartArea is defined

        const { ctx, chartArea: { width, height } } = chart;

        chart.data.labels.forEach((label, i) => {
            const meta = chart.getDatasetMeta(0);
            const { x, y } = meta.data[i].tooltipPosition();

            const image = new Image();
            image.src = categoryIcons[label];

            ctx.drawImage(image, x - 12, y - 12, 24, 24);
        });
    };

    return (
        <div className="chart-container">
            <Bar data={barData} />
            <Pie data={pieData} options={{ plugins: { legend: { display: false }, tooltip: { enabled: false } }, animation: { onComplete: drawIcons } }} />
        </div>
    );
};

export default Chart;
