import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';
import { useEffect } from 'react';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const categoryIcons = {
    personal: 'icon-personal.png',
    work: 'icon-work.png',
    tasks: 'icon-tasks.png',
    other: 'icon-other.png',
};

const Chart = ({ notes }) => {
    const categories = ['personal', 'work', 'tasks', 'other'];
    const categoryColors = {
        personal: '#aec6cf',
        work: '#d3b8ae',
        tasks: '#c6e2e9',
        other: '#d6b8db',
    };
    const categoryCounts = categories.map(category => notes.filter(note => note.category === category).length);

    const barData = {
        labels: categories,
        datasets: [{
            label: 'Número de Notas',
            data: categoryCounts,
            backgroundColor: categories.map(category => categoryColors[category]),
        }]
    };

    const pieData = {
        labels: categories,
        datasets: [{
            data: categoryCounts,
            backgroundColor: categories.map(category => categoryColors[category]),
        }]
    };

    const piePlugins = [
        {
            id: 'categoryIcons',
            afterDraw: (chart) => {
                const { ctx, chartArea: { top, width }, scales: { r } } = chart;
                const offsetY = -10; // Ajusta la posición vertical de los iconos
                chart.config.data.labels.forEach((label, i) => {
                    const angle = (chart.getDatasetMeta(0).data[i].startAngle + chart.getDatasetMeta(0).data[i].endAngle) / 2;
                    const x = r.xCenter + (r.drawingArea + offsetY) * Math.cos(angle);
                    const y = r.yCenter + (r.drawingArea + offsetY) * Math.sin(angle);

                    const img = new Image();
                    img.src = categoryIcons[label];
                    ctx.drawImage(img, x - 15, y - 15, 30, 30); // Ajusta el tamaño de los iconos según sea necesario
                });
            }
        }
    ];

    return (
        <div className="chart-container">
            <Bar data={barData} />
            <div className="mt-4">
                <Pie data={pieData} plugins={piePlugins} />
            </div>
        </div>
    );
};

export default Chart;
