import React, { useRef, useEffect } from 'react';
import Chart from 'chart.js/auto';

const ChartComponent = ({ notes }) => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const ctx = canvasRef.current.getContext('2d');

        const categories = ['personal', 'work', 'tasks', 'other'];
        const categoryIcons = {
            personal: 'icon-personal.png',
            work: 'icon-work.png',
            tasks: 'icon-tasks.png',
            other: 'icon-other.png',
        };

        const categoryCounts = categories.map(category => notes.filter(note => note.category === category).length);

        const colors = ['#8eb8e5', '#d3b8ae', '#6cbf6f', '#d6b8db'];

        const images = categories.map(category => {
            const img = new Image();
            img.src = categoryIcons[category];
            return img;
        });

        const data = {
            labels: categories,
            datasets: [{
                data: categoryCounts,
                backgroundColor: colors,
            }]
        };

        const myPieChart = new Chart(ctx, {
            type: 'pie',
            data: data,
            options: {
                responsive: true,
                plugins: {
                    legend: {
                        display: true
                    }
                }
            },
            plugins: [{
                id: 'centerIcons',
                afterDatasetsDraw: (chart) => {
                    const { ctx, chartArea: { left, top, width, height } } = chart;
                    const dataset = chart.getDatasetMeta(0).data;

                    dataset.forEach((data, index) => {
                        const { x, y } = data.tooltipPosition();
                        const img = images[index];
                        ctx.drawImage(img, x - 15, y - 15, 30, 30);
                    });
                }
            }]
        });

        return () => {
            myPieChart.destroy();
        };
    }, [notes]);

    return <canvas ref={canvasRef} />;
};

export default ChartComponent;
