import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const Chart = ({ notes }) => {
    const categories = ['work', 'personal', 'urgent'];
    const categoryCounts = categories.map(category => notes.filter(note => note.category === category).length);

    const barData = {
        labels: categories,
        datasets: [{
            label: 'Número de Notas',
            data: categoryCounts,
            backgroundColor: ['#ff6384', '#36a2eb', '#cc65fe']
        }]
    };

    const pieData = {
        labels: categories,
        datasets: [{
            data: categoryCounts,
            backgroundColor: ['#ff6384', '#36a2eb', '#cc65fe']
        }]
    };

    return (
        <div className="chart-container my-4">
            <h2 className="text-xl font-semibold mb-2">Distribución de Notas por Categoría</h2>
            <div className="flex flex-wrap justify-around">
                <div className="w-full md:w-1/2 p-2">
                    <Bar data={barData} />
                </div>
                <div className="w-full md:w-1/2 p-2">
                    <Pie data={pieData} />
                </div>
            </div>
        </div>
    );
};

export default Chart;
