import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const categoryIcons = {
    personal: 'icon-personal.png',
    work: 'icon-work.png',
    tasks: 'icon-tasks.png',
    other: 'icon-other.png',
};

const Chart = ({ notes }) => {
    const categories = ['personal', 'work', 'tasks', 'other'];
    const categoryColors = {
        personal: '#aec6cf',
        work: '#d3b8ae',
        tasks: '#c6e2e9',
        other: '#d6b8db',
    };
    const categoryCounts = categories.map(category => notes.filter(note => note.category === category).length);

    const barData = {
        labels: categories,
        datasets: [{
            label: 'Número de Notas',
            data: categoryCounts,
            backgroundColor: categories.map(category => categoryColors[category]),
        }]
    };

    const pieData = {
        labels: categories,
        datasets: [{
            data: categoryCounts,
            backgroundColor: categories.map(category => categoryColors[category]),
        }]
    };

    const plugins = [{
        afterDraw: (chart) => {
            const { ctx, chartArea: { top, bottom, left, right, width, height } } = chart;
            const centerX = (left + right) / 2;
            const centerY = (top + bottom) / 2;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.font = '16px Arial';

            categories.forEach((category, index) => {
                const angle = (index / categories.length) * (2 * Math.PI) - Math.PI / 2;
                const x = centerX + (width / 3) * Math.cos(angle);
                const y = centerY + (height / 3) * Math.sin(angle);
                const image = new Image();
                image.src = categoryIcons[category];
                ctx.drawImage(image, x - 15, y - 15, 30, 30);
            });
        }
    }];

    return (
        <div className="chart-container">
            <Bar data={barData} />
            <div className="mt-4">
                <Pie data={pieData} plugins={plugins} />
            </div>
        </div>
    );
};

export default Chart;
