import React from 'react';

const CategoryFilter = ({ filter, setFilter }) => {
    return (
        <div className="category-filter mb-4 flex space-x-2">
            <button className={`px-4 py-2 rounded ${filter === '' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`} onClick={() => setFilter('')}>Todas</button>
            <button className={`px-4 py-2 rounded ${filter === 'work' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`} onClick={() => setFilter('work')}>Trabajo</button>
            <button className={`px-4 py-2 rounded ${filter === 'personal' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`} onClick={() => setFilter('personal')}>Personal</button>
            <button className={`px-4 py-2 rounded ${filter === 'tasks' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`} onClick={() => setFilter('tasks')}>Tareas</button>
            <button className={`px-4 py-2 rounded ${filter === 'other' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`} onClick={() => setFilter('other')}>Otro</button>
        </div>
    );
};

export default CategoryFilter;
